mapper.select:
SELECT  username
FROM user_manager
WHERE id=#{id}

SELECT *
FROM user_manager
WHERE 1=1

  AND  phone_number = #{phoneNumber}


  AND  work_id = #{workId}

  AND username = #{username})

SELECT count(*)
FROM user_manager
WHERE 1=1

  AND  phone_number = #{phoneNumber}


  AND  work_id = #{workId}

  AND username = #{username}

SELECT *
FROM user_manager
WHERE  phone_number = #{phoneNumber} AND password=#{password}

SELECT *
FROM user_manager
WHERE 1=1

  AND  phone_number = #{phoneNumber}

    AND sign_time >= #{sSignDate}        
    AND sign_time <= #{eSignDate}  

  AND  work_id = #{workId}


  AND  role_id = #{roleId}

  	AND username LIKE CONCAT(CONCAT('%',#{username}),'%')	 
        ORDER BY ${sortName} ${sortOrder} 
         limit #{startRecord} , #{pageSize}

SELECT count(*)
FROM user_manager
WHERE 1=1

  AND  phone_number = #{phoneNumber}

    AND sign_time >= #{sSignDate}        
    AND sign_time <= #{eSignDate}  

  AND  work_id = #{workId}


  AND  role_id = #{roleId}

  	AND username LIKE CONCAT(CONCAT('%',#{username}),'%')	 
        ORDER BY ${sortName} ${sortOrder} 
         limit #{startRecord} , #{pageSize}

mapper.update:
update user_manager
set  role_id = #{roleId}
where id = #{id}
