mapper.select:
SELECT *
FROM book
WHERE 1=1
   AND userable_num>0 
   AND  professional_type1 = #{professionalType1,jdbcType=INTEGER}   
   AND  professional_type2 = #{professionalType2,jdbcType=INTEGER}   
   AND  ISBN = #{isbn} 
  	AND press LIKE CONCAT(CONCAT('%',#{press}),'%')	 
  	AND book_name LIKE CONCAT(CONCAT('%',#{bookName}),'%')	 
  	AND author LIKE CONCAT(CONCAT('%',#{author}),'%')	 
  	AND professional_type_name1 LIKE CONCAT(CONCAT('%',#{professionalTypeName1}),'%')	 
  	AND professional_type_name2 LIKE CONCAT(CONCAT('%',#{professionalTypeName2}),'%')	 
  	AND book_type_name1 LIKE CONCAT(CONCAT('%',#{bookTypeName1}),'%')	 
  	AND book_type_name2 LIKE CONCAT(CONCAT('%',#{bookTypeName2}),'%')	 
   AND  sell_status = #{sellStatus} 
   AND  borrow_status = #{borrowStatus} 
   AND  gift_status = #{giftStatus} 
   AND  auction_status = #{auctionStatus} 
        ORDER BY ${sortName} ${sortOrder} 
         limit #{startRecord} , #{pageSize}

SELECT *
FROM book
WHERE userable_num>0
   AND  professional_type1 = #{professionalType1,jdbcType=INTEGER}   
   AND  professional_type2 = #{professionalType2,jdbcType=INTEGER}   
  	AND press LIKE CONCAT(CONCAT('%',#{press}),'%')	 
  	AND book_name LIKE CONCAT(CONCAT('%',#{bookName}),'%')	 
  	AND author LIKE CONCAT(CONCAT('%',#{author}),'%')	 
  	AND professional_type_name1 LIKE CONCAT(CONCAT('%',#{professionalTypeName1}),'%')	 
  	AND professional_type_name2 LIKE CONCAT(CONCAT('%',#{professionalTypeName2}),'%')	 
  	AND book_type_name1 LIKE CONCAT(CONCAT('%',#{bookTypeName1}),'%')	 
  	AND book_type_name2 LIKE CONCAT(CONCAT('%',#{bookTypeName2}),'%')	 
   AND  sell_status = #{sellStatus} 
   AND  borrow_status = #{borrowStatus} 
   AND  gift_status = #{giftStatus} 
   AND  auction_status = #{auctionStatus} 

        ORDER BY ${sortName} ${sortOrder} 
 limit #{pageSize}

SELECT count(*)
FROM book
WHERE 1=1
   AND  professional_type1 = #{professionalType1,jdbcType=INTEGER}   
   AND  professional_type2 = #{professionalType2,jdbcType=INTEGER}   
   AND  ISBN = #{isbn} 
  	AND press LIKE CONCAT(CONCAT('%',#{press}),'%')	 
  	AND book_name LIKE CONCAT(CONCAT('%',#{bookName}),'%')	 
  	AND author LIKE CONCAT(CONCAT('%',#{author}),'%')	 
  	AND professional_type_name1 LIKE CONCAT(CONCAT('%',#{professionalTypeName1}),'%')	 
  	AND professional_type_name2 LIKE CONCAT(CONCAT('%',#{professionalTypeName2}),'%')	 
   AND  sell_status = #{sellStatus} 
   AND  borrow_status = #{borrowStatus} 
